-- 실행 횟수 상위 5개 SQL_ID 확인
SELECT SQL_ID, EXECUTIONS
FROM V$SQL
WHERE UPPER(SQL_TEXT) LIKE '%REVIEW_STATISTICS%'
  AND SQL_TEXT NOT LIKE '%V$SQL%'
  AND SQL_TEXT NOT LIKE '%explain plan%'
ORDER BY EXECUTIONS DESC
FETCH FIRST 5 ROWS ONLY;

