-- 과정 2 : IN절 내 바인드 변수만 정확히 계산하여 SOL_ID 계산
SELECT 
    SQL_ID,
    EXECUTIONS,
    BUFFER_GETS,
    ROUND(ELAPSED_TIME / NULLIF(EXECUTIONS, 0) / 1000, 2) AS AVG_MS,
    -- 전체 콤마에서 SELECT절 콤마(5개) 제외
    LENGTH(SQL_FULLTEXT) - LENGTH(REPLACE(SQL_FULLTEXT, ',', '')) - 4 AS BIND_COUNT,
    LENGTH(SQL_FULLTEXT) AS SQL_LENGTH
FROM V$SQL
WHERE UPPER(SQL_TEXT) LIKE '%REVIEW_STATISTICS%'
  AND SQL_TEXT NOT LIKE '%V$SQL%'
  AND SQL_TEXT NOT LIKE '%explain plan%'
ORDER BY BIND_COUNT DESC
FETCH FIRST 10 ROWS ONLY;

-- 과정 3 : 과정 2의 결과에 따라 확인된 SOL_ID 를 가지고 검색 시도 (현재는 상위 2개)
-- 두 SQL_ID의 SQL 구문 확인 (앞 500자만)
SELECT 
    SQL_ID,
    SUBSTR(SQL_FULLTEXT, 1, 500) AS SQL_TEXT_PREVIEW
FROM V$SQL
WHERE SQL_ID IN ('0spc8zt4hw331', '3h4xjsw54v4h1');

-- 과정 4 : 이제 실제 확인된 sol_id 값(0spc8zt4hw331)을 사용하여 실행계획 조회
SELECT * FROM TABLE(DBMS_XPLAN.DISPLAY_CURSOR(
    sql_id => '0spc8zt4hw331',
    format => 'TYPICAL'
));

-- ==== 모든 SQL_ID의 PLAN_HASH_VALUE 비교 ====
-- PLAN_HASH_VALUE가 같으면 실행계획이 동일
SELECT 
    p.SQL_ID,
    s.EXECUTIONS,
    p.PLAN_HASH_VALUE,
    COUNT(*) OVER (PARTITION BY p.PLAN_HASH_VALUE) AS SAME_PLAN_COUNT
FROM V$SQL_PLAN p
JOIN V$SQL s ON p.SQL_ID = s.SQL_ID
WHERE p.SQL_ID IN (
    SELECT SQL_ID
    FROM V$SQL
    WHERE UPPER(SQL_TEXT) LIKE '%REVIEW_STATISTICS%'
      AND SQL_TEXT NOT LIKE '%V$SQL%'
      AND SQL_TEXT NOT LIKE '%explain plan%'
)
AND p.ID = 0  -- 최상위 노드만
ORDER BY s.EXECUTIONS DESC;